#ifndef __SYSCTRL_H__
#define __SYSCTRL_H__

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <nan.h>
#include <list>
#include <string>

NAN_METHOD(NodeConnect);
void EIO_NodeConnect(uv_work_t* req);
void EIO_AfterNodeConnect(uv_work_t* req);

NAN_METHOD(NodeCtrl);
void EIO_NodeCtrl(uv_work_t* req);
void EIO_AfterNodeCtrl(uv_work_t* req);

NAN_METHOD(NodeClose);
void EIO_NodeClose(uv_work_t* req);
void EIO_AfterNodeClose(uv_work_t* req);

struct NodeConnBaton {
  char ipaddr[16];
  int port;
  char user[24];
  char pass[24];
  int userId;
  int playHandle;
  Nan::Callback callback;
};

struct NodeCtrlBaton {
  int playHandle;
  int ctrlFlag;
};

struct NodeCloseBaton {
  int userId;
  int playHandle;
};

#endif //__SYSCTRL_H__