SysCtrl = require('./sysctrl.js');

var mytest = new SysCtrl({
	ipaddr: '192.168.1.88',
	port: 9008,
	user: 'admin',
	pass: '123456'
}, function(userId, playHandle) {
	console.log('ctrl userId: ' + userId + ', playHandle: ' + playHandle);

	if(userId > 0) {
		mytest.netcamctrl(playHandle, mytest.PTZ_CMD_RIGHT);
	}

	setTimeout(function() {
		console.log('closed....');
		mytest.netcamclose(userId, playHandle);
	}, 60000);
});